mapper.select:
SELECT
SUM(t.ticket_price) as count,
to_char(payment_date,'YYYY') as payment_date
FROM sales, ticket t
WHERE 
payment_date >= #{startDate}
AND payment_date < #{endDate}

GROUP BY to_char(payment_date, 'YYYY');

SELECT
SUM(t.ticket_price) as count,
to_char(payment_date,'YYYY-MM') as payment_date
FROM sales
WHERE 
payment_date >= #{startDate}
AND payment_date < #{endDate}

GROUP BY to_char(payment_date, 'YYYY-MM');
