mapper.update:
UPDATE review
SET review_content = #{reviewContent}, review_score = #{reviewScore}
WHERE
email = #{email} AND movie_id = #{movieId}

mapper.select:
		SELECT * FROM (SELECT ROWNUM rnum, R.* FROM
		(SELECT r.*, m.movie_title, mem.nick FROM review r, movie m, member mem
    	WHERE r.movie_id = m.movie_id AND r.email = mem.email

		AND r.email = #{email}


		AND r.movie_id = #{movieId}

			) R
		)
		WHERE 1=1

		AND rnum BETWEEN #{start} AND #{end} 

		ORDER BY review_id DESC
