mapper.update:
UPDATE review
SET review_content=#{reviewContent}
WHERE
email = #{email}

mapper.select:
SELECT r.review_id, r.movie_id, m.movie_title, r.email, mem.nick, r.review_content, r.review_score, r.like_count, r.review_reg_date FROM review r, movie m, member mem WHERE r.movie_id = m.movie_id AND r.email = mem.email
AND r.movie_id = #{movieId}
AND r.email = #{email}
ORDER BY review_id DESC
